import collections
class Solution:
    def numTilings(self, n: int) -> int:
        hsh = {1:1, 2:2, 3:5}
        mod = 10**9+7

        @lru_cache(maxsize = 100)
        def helper(n):
            if n <=3: return hsh[n]
            else: return (2 * helper(n-1)+helper(n-3))%mod

        return helper(n)


def main():
    sol = Solution()
    print(sol.numTilings(94))

if __name__ == "__main__": main()

